    private void btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnActionPerformed
        int numero = Integer.parseInt(Dato1.getText());
        int numero2 = Integer.parseInt(Dato2.getText());
        int operador = Integer.parseInt(Operador.getText());
        int resultadoFinal = calculadora(numero, numero2, operador);
        resultado.setText(""+resultadoFinal);
         
    }//GEN-LAST:event_btnActionPerformed

