    private static int calculadora(int dato1, int dato2, int operador) {
        WS.Calculadora_Service service = new WS.Calculadora_Service();
        WS.Calculadora port = service.getCalculadoraPort();
        return port.calculadora(dato1, dato2, operador);
    }
